package com.edubridge.typecasting;

public class TypeCastingDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char str ='a';
		int character = str;
		System.out.println(character);
		
		
		int num3 = 63;
		char x = (char) num3;
		System.out.println(x);

	}

}
